#include<stdio.h>

int main()
{
	int a,b,c,d;
	scanf("%d%d",&a,&b);
	c=a/b;
	d=a%b;
	printf("%d ",c);
	printf("%d\n",d);
	return 0;
}

#include<stdio.h>

int main()
{
	int a,b,c,d;
	scanf("%d%d",&a,&b);
	c=a/b;
	d=a%b;
	printf("%d %d",c,d);
	return 0;
}

