#include<stdio.h>

int main()
{
	int a,b,c;
	scanf("%d",&a);
	b=a/10;
	c=a%10;
	printf("%d %d",b,c);
	return 0;
}

var st:string;
    i:longint;
begin
  readln(st);
  for i:=1 to length(st) do
  if i<>length(st) then write(st[i],' ') else writeln(st[i]);
end.
