#include<stdio.h>

int main()
{
	int a,b,c,d,e,f;
	scanf("%d",&a);
	b=a/10000;
	c=a/1000%10;
	d=a/100%10;
	e=a/10%10;
	f=a%10;
	printf("%d---%d---%d---%d---%d",b,c,d,e,f);
	return 0; 
}

var st:string;
    i:longint;
begin
  readln(st);
  for i:=1 to length(st) do
  if i<>length(st) then write(st[i],'---') else writeln(st[i]);
end.

