#include<stdio.h>

int main()
{
	int a,b,c,d,e,f;
	scanf("%d%d",&a,&b);
	c=a+b;
	d=a-b;
	e=a*b;
	f=a/b;
	printf("%d+%d=%d\n%d-%d=%d\n%d*%d=%d\n%d/%d=%d\n",a,b,c,a,b,d,a,b,e,a,b,f);
	return 0;
}

#include<stdio.h>

int main()
{
	int a,b,c;
	scanf("%d%d",&a,&b);
	c=a+b;
	printf("%d+%d=%d\n",a,b,c);
	c=a-b;
	printf("%d-%d=%d\n",a,b,c);
	c=a*b;
	printf("%d*%d=%d\n",a,b,c);
	c=a/b;
	printf("%d/%d=%d\n",a,b,c);
	return 0;
}

var a,b:integer;
begin
  readln(a,b);
  writeln(a,'+',b,'=',a+b);
  writeln(a,'-',b,'=',a-b);
  writeln(a,'*',b,'=',a*b);
  if a mod b =0 then
  writeln(a,'/',b,'=',a/b:0:0);
end.

#include<stdio.h>

int main()
{
	float a,b,c,d,e,f;
	scanf("%f%f",&a,&b);
	c=a+b;
	d=a-b;
	e=a*b;
	f=a/b;
	printf("%.2f+%.2f=%.2f\n",a,b,c);
	printf("%.2f-%.2f=%.2f\n",a,b,d);
	printf("%.2f*%.2f=%.2f\n",a,b,e);
	printf("%.2f/%.2f=%.2f\n",a,b,f);
	return 0;
}

